import React, { useEffect, useState } from "react";
import "./RegisterPage.css";

const RegisterPage = () => {

  const [user, setUser] = useState({ userName: '', password: '', email: '' });

  const handleSubmit = (event) => {
    event.preventDefault();

    const user = {
      username: user.username,
      password: user.password,
      email: user.email,
    };

    fetch("http://localhost:8080/user/register", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(user),
    })
      .then((response) => {
        if (response.ok) {
          return response.text();
        } else {
          throw new Error("Failed to register user");
        }
      })
      .then((data) => {
        alert("Registration successful!"); // Thông báo đăng ký thành công
        // Xử lý logic sau khi đăng ký thành công (ví dụ: điều hướng trang, cập nhật giao diện, v.v.)
        if (data.id) {
          localStorage.setItem("token", data.role === 1 ? "admin" : "client");
          localStorage.setItem("username", data.userName);
          localStorage.setItem("userId", data.id);
          window.location.href = "/library";
        }
      })
      .catch((error) => {
        alert("Registration failed: " + error.message); // Thông báo lỗi đăng ký
        console.error(error); // In ra lỗi để xử lý
      });
  };


  return (
    <div className="register-page">
      <div className="register-form">
        <h2>Register</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input type="email" id="email" onChange={(e) => { setUser({ ...user, email: e.target.value }); console.log(user) }  } />
          </div>
          <div className="form-group">
            <label htmlFor="username">Username</label>
            <input type="text" id="username" onChange={(e) => setUser({ ...user, userName: e.target.value })} />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input type="password" id="password" onChange={(e) => setUser({ ...user, password: e.target.value })} />
          </div>
          <button type="submit" className="register-btn">
            Register
          </button>
        </form>
      </div>
    </div>
  );
};

export default RegisterPage;
