import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./Header.css";

const Header = () => {
  const [isLoggedIn, setLoggedIn] = useState(
    localStorage.getItem("token") !== null
  );

  const handleLogout = () => {
    localStorage.removeItem("token");
    setLoggedIn(false);
    window.location.reload();
  };

  return (
    <header className="header">
      <div className="container">
        <div className="row align-items-center">
          <div className="col-1">
            <Link to="/library" className="btn btn-warning">
               Trang chủ
            </Link>
          </div>
          <div >
            <Link to="/order" className="btn btn-warning">
              Lịch sử
            </Link>
          </div>


          {/* <div className="col-3">
            <img
              src="https://upload.wikimedia.org/wikipedia/commons/1/13/Logo_PTIT_University.png"
              alt="Logo"
              className="logo"
            />
          </div> */}

          <div className="col-6 text-center">
            <h1 className="header-title"> {isLoggedIn ? localStorage.getItem("username") : "B20DCCN297 - Nguyễn Tiến Hùng"} </h1>
          </div>

          <div className="col-3 text-right">
            {
              isLoggedIn ? (
                <button className="btn btn-danger" onClick={handleLogout}>
                  Log Out
                </button>
              ) : (
                <div>
                  <Link to="/login" className="btn btn-success">
                    Login
                  </Link>{" "}
                  <Link to="/register" className="btn btn-primary">
                    Register
                  </Link>
                </div>
              )}
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
