import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./Header.css";

const Header = () => {
  const [isLoggedIn, setLoggedIn] = useState(
    localStorage.getItem("token") !== null
  );

  const navigation = useNavigate();
  const handleLogout = () => {
    localStorage.removeItem("token");
    setLoggedIn(false);
    // navigation("/library");
    window.location.reload()
  };

  return (
    <header className="header">
      <div className="container">
        <div className="row align-items-center">
          <div className="col-3">
            <img
              src="https://upload.wikimedia.org/wikipedia/commons/1/13/Logo_PTIT_University.png"
              alt="Logo"
              className="logo"
            />
          </div>
          <div className="col-6 text-center">
            <h1 className="header-title">B20DCCN297 - Nguyễn Tiến Hùng</h1>
          </div>
          <div className="col-3 text-right">
            {isLoggedIn ? (
              <button className="btn btn-danger" onClick={handleLogout}>
                Log Out
              </button>
            ) : (
              <div>
                <Link to="/library/login" className="btn btn-success">
                  Login
                </Link>{" "}
                <Link to="/library/register" className="btn btn-primary">
                  Register
                </Link>
              </div>
            )}
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
