import "./LoginPage.css";
import { useState } from "react";
import { useNavigate } from "react-router-dom";

const LoginPage = () => {

  // khai báo ràng buộc: muốn thay đổi username, phải dùng hàm setUsername
  // useState("") với "" là giá trị mặc định cho username
  const [user, setUser] = useState();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Gửi yêu cầu đăng nhập thông qua API
    fetch(`http://localhost:8080/user/login?username=${username}&password=${password}`)
      .then((response) => response.data())
      // .then((token) => {
      //   // Đăng nhập thành công: admin | client
      //   if (token === "admin" || token === "client") {
      //     // alert("Đăng nhập thành công");
      //     localStorage.setItem("token", token);
      //     localStorage.setItem("username", username);
      //     navigate("/library");
      //   }
      //   // Đăng nhập thất bại
      //   else {
      //     alert("Tài khoản không tồn tại hoặc mật khẩu không chính xác!");
      //   }
      // })
      .then((data) => {
        setUser(data);
        console.log("data user:" + data.id);
        localStorage.setItem("token", data.role === 1 ? "admin" : "client");
        localStorage.setItem("username", data.userName);
        localStorage.setItem("idUser", data.id);
        navigate("/library");
      })
      .catch((error) => alert(error));
  };

  return (
    <div className="login-page">
      <div className="login-form">
        <h2>Login</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={handleUsernameChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={handlePasswordChange}
            />
          </div>
          <button type="submit" className="btn-login">
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default LoginPage;
