import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import "./BookClient.css"

const BookClient = () => {
  const params = useParams();
  const id = params.id;

  const [bookItem, setBookItem] = useState({});

  const [quantity, setQuantity] = useState(1);
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState("");
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:8080/book/${id}`)
      .then((response) => response.json())
      .then((data) => setBookItem(data))
      .catch((err) => console.log(err));
  }, []);

  // Xử lý thêm vào giỏ hàng
  const handleAddToCart = () => {
    // Tạo đối tượng đại diện cho đơn hàng
    const order = {
      idUser: localStorage.getItem("userId"),
      idBook: bookItem.id,
      sum: quantity
    };

    console.log(order);

    // Thực hiện gửi yêu cầu đặt hàng đến máy chủ
    fetch("http://localhost:8080/orders", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(order)
    })
      .then((response) => {
        if (response.ok) {
          alert("Mua thành công!");
          // Xử lý thành công
          console.log("Order placed successfully");
        } else {
          alert("Failed to place order");
          // Xử lý lỗi
          console.log("Failed to place order");
        }
      })
      .catch((error) => {
        console.log("Error:", error);
      });
  };

  const handleQuantityChange = (event) => {
    setQuantity(parseInt(event.target.value));
  };

  const handleRatingChange = (event) => {
    setRating(parseInt(event.target.value));
  };

  const handleCommentChange = (event) => {
    setComment(event.target.value);
  };

  const handleAddReview = () => {
    // Xử lý thêm đánh giá
    if (rating === 0 || comment.trim() === "") {
      alert("Please select a rating and enter a comment");
      return;
    }

    const newReview = {
      idBook: id,
      idUser: localStorage.getItem("userId"),
      starCnt: rating,
      comment: comment
    };

    fetch("http://localhost:8080/rating", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(newReview)
    })
      .then((response) => {
        if (response.ok) {
          alert("Review added successfully");
          setReviews([...reviews, newReview]);
          setRating(0);
          setComment("");
        } else {
          alert("Failed to add review");
          console.log("Failed to add review");
        }
      })
      .catch((error) => {
        console.log("Error:", error);
      });
  };

  return (
    <>
      <br> </br>
      <div className="container">
        <div className="row">
          <div className="col-3">
            <img
              src={bookItem.cover}
              alt={bookItem.title}
              className="img-fluid"
            />
          </div>
          <div className="col-8">
            <h1>{bookItem.title}</h1>
            <p>Tác giả: {bookItem.author}</p>
            <p>Thể loại: {bookItem.category}</p>
            <div className="d-flex align-items-center">
              <input
                className="quantity-purchased"
                type="number"
                id="quantity"
                min="1"
                value={quantity}
                onChange={handleQuantityChange}
              />
              <button className="btn btn-success" onClick={handleAddToCart}>Mua</button>
            </div>
          </div>
        </div>

        <div className="row">
          <div className="col-12">
            <h2>Reviews</h2>
            <div className="row">
              <div className="col-12">
                <div className="form-group">
                  <label htmlFor="rating">Rating</label>
                  <select
                    className="form-control"
                    id="rating"
                    value={rating}
                    onChange={handleRatingChange}
                  >
                    <option value="0">Select rating</option>
                    <option value="1">1 star</option>
                    <option value="2">2 stars</option>
                    <option value="3">3 stars</option>
                    <option value="4">4 stars</option>
                    <option value="5">5 stars</option>
                  </select>
                </div>
                <div className="form-group">
                  <label htmlFor="comment">Comment</label>
                  <textarea
                    className="form-control"
                    id="comment"
                    rows="3"
                    value={comment}
                    onChange={handleCommentChange}
                  ></textarea>
                </div>
                <button className="btn btn-primary" onClick={handleAddReview}>
                  Add review
                </button>
              </div>
            </div>
            <div className="row">
              <div className="col-12">
                <ul className="list-group">
                  {bookItem.reviews?.map((review) => (
                    <li className="list-group-item" key={review.id}>
                      <p>Rating: {review.rating}</p>
                      <p>Comment: {review.comment}</p>
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default BookClient;
