import React, { useState, useEffect } from "react";

const BookTableBody = () => {
  const [books, setBooks] = useState([]);
  const [remain, setRemain] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/books")
      .then((response) => response.json())
      .then((data) => {
        setBooks(data);
        setRemain(data);
      })
      .catch((err) => console.log(err));
  }, []);

  return (
    <div className="container">
      <br />
      <table className="table table-striped table-bordered">
        <thead className="table-dark">
          <tr>
            <th className="text-center align-middle">Tiêu đề</th>
            <th className="text-center align-middle">Tác giả</th>
            <th className="text-center align-middle">Thể loại</th>
            <th className="text-center align-middle">Ngày phát hành</th>
            <th className="text-center align-middle">Số trang</th>
            <th className="text-center align-middle">Số lượng đã bán</th>
            <th className="text-center align-middle">Action</th>
          </tr>
        </thead>
        <tbody>
          {remain.map((book) => (
            <tr key={book.id}>
              <td className="text-center align-middle">{book.title}</td>
              <td className="text-center align-middle">{book.author}</td>
              <td className="text-center align-middle">{book.category}</td>
              <td className="text-center align-middle">{book.releaseDate}</td>
              <td className="text-center align-middle">{book.pageNumber}</td>
              <td className="text-center align-middle">{book.soldNumber}</td>
              <td className="text-center align-middle">
                {localStorage.getItem("token") === "admin" && (
                  <div className="d-flex justify-content-between">
                    <button className="btn btn-primary">View</button>
                    <button className="btn btn-danger">Delete</button>
                  </div>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="d-flex justify-content-center">
        <button className="btn btn-primary">Add New</button>
      </div>
    </div>
  );
};

export default BookTableBody;
