import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import "./BookClient.css"; // Import file CSS cho BookClient

const BookClient = () => {
  const params = useParams();
  const id = params.id;

  const [bookItem, setBookItem] = useState({});
  const [category, setCategory] = useState([]);
  const [editing, setEditing] = useState(id < 0 ? true : false);
  const [quantity, setQuantity] = useState(1);
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState("");

  useEffect(() => {
    fetch(`http://localhost:8080/books/${id}`)
      .then((response) => response.json())
      .then((data) => setBookItem(data))
      .catch((err) => console.log(err));
  }, []);

  const handleAddToCart = () => {
    // Xử lý thêm vào giỏ hàng
  };

  const handleQuantityChange = (event) => {
    setQuantity(parseInt(event.target.value));
  };

  const handleRatingChange = (event) => {
    setRating(parseInt(event.target.value));
  };

  const handleCommentChange = (event) => {
    setComment(event.target.value);
  };

  const handleAddReview = () => {
    // Xử lý thêm đánh giá
  };

  return (
    <div className="container">
      <div className="book-container">
        <div className="book-image">
          <img
            src={bookItem.cover}
            alt={bookItem.title}
            className="img-fluid"
          />
        </div>
        <div className="book-details">
          <h1 className="book-title">{bookItem.title}</h1>
          <p className="book-author">Author: {bookItem.author}</p>
          <p className="book-category">Category: {bookItem.category}</p>
          <p className="book-description">
            Description: {bookItem.description}
          </p>
          <div className="book-add-to-cart">
            <label htmlFor="quantity">Quantity:</label>
            <input
              type="number"
              id="quantity"
              min="1"
              value={quantity}
              onChange={handleQuantityChange}
              className="quantity-input"
            />
            <button className="add-to-cart-button" onClick={handleAddToCart}>
              Add to cart
            </button>
          </div>
        </div>
      </div>

      <div className="reviews-container">
        <h2 className="reviews-heading">Reviews</h2>
        <div className="review-form">
          <div className="rating-form">
            <label htmlFor="rating">Rating:</label>
            <select
              className="rating-select"
              id="rating"
              value={rating}
              onChange={handleRatingChange}
            >
              <option value="0">Select rating</option>
              <option value="1">1 star</option>
              <option value="2">2 stars</option>
              <option value="3">3 stars</option>
              <option value="4">4 stars</option>
              <option value="5">5 stars</option>
            </select>
          </div>
          <div className="comment-form">
            <label htmlFor="comment">Comment:</label>
            <textarea
              className="comment-input"
              id="comment"
              rows="3"
              value={comment}
              onChange={handleCommentChange}
            ></textarea>
          </div>
          <button className="add-review-button" onClick={handleAddReview}>
            Add review
          </button>
        </div>
        <div className="review-list">
          <ul className="list-group">
            {bookItem.reviews?.map((review) => (
              <li className="list-group-item" key={review.id}>
                <p>Rating: {review.rating}</p>
                <p>Comment: {review.comment}</p>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default BookClient;
