import React from "react";
import { useState, useEffect } from "react";

const BookTableBody = () => {
  const [book, setBook] = useState([]);
  const [remain, setRemain] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/books")
      .then((response) => response.json())
      .then((data) => setBook(data))
      .catch((err) => console.log(err));
  }, []);

  useEffect(() => {
    setRemain(book);
  }, [book]);

  return (
    <div>
      <div className="container">
        <br></br>
        <table className="table table-striped table-bordered">
          <thead className="table-dark">
            <tr>
              <th className="text-center">Tiêu đề</th>
              <th className="text-center">Tác giả</th>
              <th className="text-center">Thể loại</th>
              <th className="text-center">Ngày phát hành</th>
              <th className="text-center">Số trang</th>
              <th className="text-center">Số lượng đã bán</th>
              <th className="text-center">Action</th>
            </tr>
          </thead>
          <tbody>
            {remain.map((book) => (
              <tr key={book.id}>
                <td>{book.title}</td>
                <td>{book.author}</td>
                <td>{book.category}</td>
                <td className="text-center">{book.releaseDate}</td>
                <td className="text-center">{book.pageNumber}</td>
                <td className="text-center">{book.soldNumber}</td>
                <td >
                 { localStorage.GetItem("token") === 'admin'&&
                  (
                    <button> 
                      View
                    </button>)
                  }
                   </td>

              </tr>
            ))}
          </tbody>
        </table>

        <div className="d-flex justify-content-center">
          <button className="btn btn-primary">Add New</button>
        </div>
      </div>
    </div>
  );
};
export default BookTableBody;
