import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import "./BookClient.css"; // Import file CSS cho BookClient

const BookClient = () => {
  const params = useParams();
  const id = params.id;

  const [bookItem, setBookItem] = useState({});
  const [category, setCategory] = useState([]);
  const [editing, setEditing] = useState(id < 0 ? true : false);
  const [quantity, setQuantity] = useState(1);
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState("");

  useEffect(() => {
    fetch(`http://localhost:8080/books/${id}`)
      .then((response) => response.json())
      .then((data) => setBookItem(data))
      .catch((err) => console.log(err));
  }, []);

  const handleAddToCart = () => {
    // Xử lý thêm vào giỏ hàng
  };

  const handleQuantityChange = (event) => {
    setQuantity(parseInt(event.target.value));
  };

  const decreaseQuantity = (event) => {
    setQuantity(parseInt(event.target.value));
  };

  const increaseQuantity = (event) => {
    setQuantity(parseInt(event.target.value));
  };

  const handleRatingChange = (event) => {
    setRating(parseInt(event.target.value));
  };

  const handleCommentChange = (event) => {
    setComment(event.target.value);
  };

  const handleAddReview = () => {
    // Xử lý thêm đánh giá
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col-3">
          <img
            src={bookItem.cover}
            alt={bookItem.title}
            className="img-fluid"
          />
        </div>
        <div className="col-8">
          <h1>{bookItem.title}</h1>
          <p>Tác giả: {bookItem.author}</p>
          <p>Thể loại: {bookItem.category}</p>
          {/* <p>Description: {bookItem.description}</p> */}
          <div className="d-flex align-items-center">
   
            <div className="quantity-input">
              <input
                className="quantity-purchased"
                type="number"
                id="quantity"
                min="1"
                value={quantity}
                onChange={handleQuantityChange}
              />
              <div className="quantity-buttons">
                <button className="quantity-button" onClick={decreaseQuantity}>
                  -
                </button>
                <button className="quantity-button" onClick={increaseQuantity}>
                  +
                </button>
              </div>
            </div>

            <button className="btn btn-success" onClick={handleAddToCart}>
              Mua
            </button>
          </div>
        </div>
      </div>

      <div className="row">
        <div className="col-12">
          <h2>Reviews</h2>
          <div className="row">
            <div className="col-12">
              <div className="form-group">
                <label htmlFor="rating">Rating</label>
                <select
                  className="form-control"
                  id="rating"
                  value={rating}
                  onChange={handleRatingChange}
                >
                  <option value="0">Select rating</option>
                  <option value="1">1 star</option>
                  <option value="2">2 stars</option>
                  <option value="3">3 stars</option>
                  <option value="4">4 stars</option>
                  <option value="5">5 stars</option>
                </select>
              </div>
              <div className="form-group">
                <label htmlFor="comment">Comment</label>
                <textarea
                  className="form-control"
                  id="comment"
                  rows="3"
                  value={comment}
                  onChange={handleCommentChange}
                ></textarea>
              </div>
              <button className="btn btn-primary" onClick={handleAddReview}>
                Add review
              </button>
            </div>
          </div>
          <div className="row">
            <div className="col-12">
              <ul className="list-group">
                {bookItem.reviews?.map((review) => (
                  <li className="list-group-item" key={review.id}>
                    <p>Rating: {review.rating}</p>
                    <p>Comment: {review.comment}</p>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BookClient;
